+  flex-direction: column;
+  align-items: center;
+  justify-content: center;
+  margin: 4px;
+  border: 3px solid #fff;
+  border-radius: 10px;
+  box-sizing: border-box;
+  overflow: hidden;
+  background-color: #555;
+  transition: transform 0.1s, background-color 0.6s;
+}
+.hero:hover {
+  background-color: #ff9c00;
+  z-index: 1;
+}
+
+/* 굿즈 이미지 스타일 */
