+  box-sizing: border-box;
+}
+.choice p {
+  border: 1px solid #555;
+  border-radius: 10px;
+  box-sizing: border-box;
+  padding: 5px;
+}
+.choice img {
+  width: 50%;
+  margin: 10px 0;
+}

