commits.content:
body {
    height: 100vh;
    background-image: url("/images/bg.jpg");
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
  }
  header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background-color: white;
    text-align: center;
    padding: 10px 0;
    z-index: 1000; /* 다른 요소 위에 위치 */
    border-bottom: none;
}

header img {
    width: 120px; /* 로고 크기 조정 */
    height: auto;
}


  .container {
    padding: 50px 0;
  }
  .heroes {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    max-width: 660px;
    margin: 0 auto;
    padding: 40px 20px;
  }
  .hero {
    width: 80px;
    height: 84px;
    margin: 4px;
    border: 3px solid #fff;
    border-radius: 10px;
    box-sizing: border-box;
    overflow: hidden;
    background-color: #555;
    transform: skewX(-14deg);
    transition:
      transform .1s,
      background-color .6s;
  }
  .hero:hover {
    background-color: #ff9c00;
    transform: skewX(-14deg) scale(1.3);
