@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;400;700&display=swap');

body {
    font-family: 'Noto Sans KR', Arial, sans-serif;
    background-color: #f2f0eb;
    margin: 0;
    padding: 0;
    text-align: center;
}

/* 전체 컨테이너 */
.container {
    max-width: 600px;
    margin: 50px auto;
    background: white;
    padding: 30px;
    box-shadow: 0px 4px 15px rgba(0, 0, 0, 0.15);
    border-radius: 12px;
    border: 2px solid #e0e0e0;
}

/* 제목 */
h1 {
    font-size: 28px;
    font-weight: 700;
    color: #006241;
    margin-bottom: 20px;
}

/* 설명 텍스트 */
p {
    font-size: 18px;
    color: #444;
    margin: 10px 0;
}

/* 입력 필드 스타일 */
input {
    padding: 12px;
    font-size: 16px;
    width: 250px;
    border: 2px solid #ccc;
    border-radius: 5px;
    outline: none;
    text-align: center;
    transition: border-color 0.3s ease;
}

input:focus {
    border-color: #006241;
}

/* 버튼 스타일 */
button {
    background-color: #006241;
    color: white;
    border: none;
    padding: 14px 22px;
    font-size: 16px;
    cursor: pointer;
    border-radius: 5px;
    transition: background 0.3s ease;
    margin-top: 15px;
    font-weight: bold;
}

button:hover {
    background-color: #004d32;
}

/* 결과 창 스타일 */
.result-box {
    background: #ffffff;
    border: 2px solid #ddd;
    padding: 20px;
    margin-top: 20px;
    border-radius: 10px;
    text-align: center;
    font-size: 18px;
    line-height: 1.6;
    color: #333;
    box-shadow: 0px 3px 10px rgba(0, 0, 0, 0.1);
}

/* 매장 정보 강조 */
.result-box strong {
    font-size: 20px;
    color: #006241;
    display: block;
    margin-bottom: 10px;
}

/* 매장명 */
.store-name {
    font-size: 22px;
    font-weight: bold;
    color: #d4a373;
    margin-bottom: 5px;
}

/* 주소 스타일 */
.store-address {
    font-size: 18px;
    color: #555;
}

/* 거리 정보 */
.store-distance {
    font-size: 18px;
    font-weight: bold;
    color: #006241;
    margin-top: 10px;
}



body { font-family: Arial, sans-serif; margin: 20px; }
hr { margin: 40px 0; }

/* 출력 결과 스타일링 */
.result-box {
    margin: 0 auto;
    width: fit-content;
  background-color: #ffffff;
  border: 2px solid #ddd;
  border-radius: 10px;
  padding: 20px;
  margin-top: 20px;
  box-shadow: 0px 3px 10px rgba(0, 0, 0, 0.1);
  text-align: center;
  font-size: 18px;
  line-height: 1.6;
  color: #333;
}
.result-box .header {
  font-size: 20px;
  font-weight: bold;
  color: #006241;
  margin-bottom: 10px;
}
.result-box .detail {
  margin-bottom: 5px;
}
.result-box .highlight {
  font-weight: bold;
  color: #d4a373;
}
